create table public.app_image_status (
object_id int8 not null,
object_code varchar(31) not null,
image_status int4,
primary key (object_id, object_code)
);

CREATE RULE app_language_add_to_imagestatus AS ON INSERT TO app_language WHERE NEW.rootobject=true
DO ALSO
INSERT INTO app_image_status(object_id,object_code,image_status) values(NEW.id,'Language',0);

CREATE RULE app_language_remove_from_imagestatus AS ON DELETE TO app_language WHERE OLD.rootobject=true
DO ALSO
DELETE FROM app_image_status where object_id=OLD.rootid and object_code='Language';

CREATE RULE app_user_add_to_imagestatus AS ON INSERT TO app_user WHERE NEW.rootobject=true
DO ALSO
INSERT INTO app_image_status(object_id,object_code,image_status) values(NEW.id,'User',0);

CREATE RULE app_user_remove_from_imagestatus AS ON DELETE TO app_user WHERE OLD.rootobject=true
DO ALSO
DELETE FROM app_image_status where object_id=OLD.rootid and object_code='User';

